function CustomPagination({setPage, numofPage=10}){
    const handlePageChange=(page)=>{
        setPage(page);
        window.scroll(0,0)
    }
    return(
        <div>
            <Pagination onChange={(e)=>handlePageChange(e.target.textContent)}/>
           
        </div>
    )
}
export default CustomPagination;
