import * as React from 'react';
import Box from '@mui/material/Box';
import BottomNavigation from '@mui/material/BottomNavigation';
import BottomNavigationAction from '@mui/material/BottomNavigationAction';
import './BottomNav.css'
import { Movie, Whatshot, Tv, Search } from '@mui/icons-material';


export default function SimpleBottomNavigation() {
  const [value, setValue] = React.useState(0);
  const history=useHistory();


  useEffect(()=>{
    if(value===0) history.push('/')
    else if(value===1) history.push('/movies')
    else if(value===2) history.push('/tvseries')
    else if(value===3) history.push('/search')
},[value, history])

  return (
    <Box className='navBar'>
      <BottomNavigation
        showLabels
        value={value}
        onChange={(event, newValue) => {
          setValue(newValue);
        }}
      >
        <BottomNavigationAction style={{backgroundColor:"rgb(246,9,9)", color:"white"}} label="Trending" icon={<Whatshot />} />
        <BottomNavigationAction style={{backgroundColor:"rgb(246,9,9)", color:"white"}} label="Movies" icon={<Movie />} />
        <BottomNavigationAction style={{backgroundColor:"rgb(246,9,9)", color:"white"}} label="TV Series" icon={<Tv />} />
        <BottomNavigationAction style={{backgroundColor:"rgb(246,9,9)", color:"white"}} label="Search" icon={<Search />} />
      </BottomNavigation>
    </Box>
  );
}
